mapper.insert:
INSERT INTO alpero.item_update(
	item_code
	,shop_code
	,title
	,price1
	,price2
	,optionText
	,thumbnailLink
	,detailLink
	,reg_date
	,upd_date
	,usr
)VALUES(
	#{itemCode}
	,#{shopCode}
	,#{title}
	,#{price1}
	,#{price2}
	,#{optionText}
	,#{thumbnailLink}
	,#{detailLink}
	,NOW()
	,NOW()
	,'admin'
)ON DUPLICATE KEY UPDATE
	title = #{title}
	,price1 = #{price1}
	,price2 = #{price2}
	,optionText = #{optionText}
	,thumbnailLink = #{thumbnailLink}
	,detailLink = #{detailLink}
	,reg_date = NOW()
	,upd_date = NOW()

mapper.update:
UPDATE alpero.item SET

		title = #{title}


		,price1 = #{price1} 


		,price2 = #{price2}


		,optionText = #{optionText}


		,thumbnailLink = #{thumbnailLink}


		,detailLink = #{detailLink}

	,reg_date = NOW()
	,upd_date = NOW()
WHERE item_code = #{itemCode}

mapper.select:
SELECT item_code as itemCode ,shop_code as shopCode ,title ,price1 ,price2 ,optionText ,thumbnailLink ,detailLink ,reg_date as regDate ,upd_date as updDate ,usr FROM alpero.item WHERE 1=1
AND item_code = #{itemCode}
AND title = #{title}
